// Function to return a random set of spells based on rarity
function getRandomSpellsByRarity(rarity, spellTable) {
    const raritySpellConfigurations = {
        "Common": [
            [{ level: 0 }, { level: 0 }, { level: 0 }],
            [{ level: 0 }, { level: 0 }, { level: 1 }],
            [{ level: 1 }, { level: 1 }]
        ],
        "Uncommon": [
            [{ level: 0 }, { level: 1 }, { level: 1 }, { level: 1 }],
            [{ level: 1 }, { level: 1 }, { level: 2 }],
            [{ level: 1 }, { level: 2 }, { level: 3 }]
        ],
        "Rare": [
            [{ level: 3 }, { level: 3 }, { level: 3 }],
            [{ level: 3 }, { level: 3 }, { level: 4 }]
        ],
        "Very Rare": [
            [{ level: 5 }, { level: 6 }, { level: 8 }],
            [{ level: 5 }, { level: 6 }, { level: 7 }],
            [{ level: 4 }, { level: 7 }, { level: 8 }]
        ],
        "Legendary": [
            [{ level: 4 }, { level: 7 }, { level: 8 }],
            [{ level: 5 }, { level: 7 }, { level: 8 }],
            [{ level: 6 }, { level: 7 }, { level: 8 }],
            [{ level: 4 }, { level: 4 }, { level: 9 }],
            [{ level: 4 }, { level: 5 }, { level: 9 }]
        ]
    };

    const configurations = raritySpellConfigurations[rarity];
    if (!configurations) throw new Error(`Invalid rarity: ${rarity}`);

    // Pick a random configuration
    const selectedConfig = configurations[Math.floor(Math.random() * configurations.length)];

    // Match spells from the spell table
    return selectedConfig.map(({ level }) => {
        // Filter spells that match the level or are lower and have highmagic = Yes
        const matchingSpells = spellTable.filter(spell =>
            parseInt(spell.level) === level || (parseInt(spell.level) < level && spell.highmagic === "Yes")
        );

        if (matchingSpells.length === 0) {
            throw new Error(`No spells available for level ${level}`);
        }

        // Randomly select a spell from the filtered list
        const selectedSpell = matchingSpells[Math.floor(Math.random() * matchingSpells.length)];

        // Store original level before any modification
        selectedSpell.originalLevel = selectedSpell.level;

        // Adjust the spell's level if necessary
        if (parseInt(selectedSpell.level) < level && selectedSpell.highmagic === "Yes") {
            selectedSpell.level = level; // Temporarily increase the level for output
        }

        return selectedSpell;
    });
}

