let curseStage = 0; // Track the stage of the curse

// Function to toggle the curse state
function toggleCurse() {
const curseButton = document.getElementById('curseButton');

// Cycle through the stages: 0 -> No Curses, 1 -> Some Curses, 2 -> All Cursed
    if (curseStage === 0) {
    curseButton.className = 'some-curses';
    curseButton.textContent = 'Some Curses';
    curseStage = 1;

    } else if (curseStage === 1) {
    curseButton.className = 'all-cursed';
    curseButton.textContent = 'All Cursed';
    curseStage = 2;

    } else if (curseStage === 2) {
    curseButton.className = 'no-curses';
    curseButton.textContent = 'No Curses';
    curseStage = 0;
    }
    }

