function generateReCharge(item, rarity) {
    if (item.category !== "Spell Focus") {
        return "";  // Only process Spell Focus items
    }

    const chargeTable = {
        "Common": ["1d4"],
        "Uncommon": ["1d4", "1d6"],
        "Rare": ["1d6", "1d8"], 
        "Very Rare": ["2d8", "1d12"],
        "Legendary": ["1d8", "1d10"]
    };

    const possibleCharges = chargeTable[rarity];
    if (!possibleCharges) {
        return "";  // Return empty if no charges are defined for this rarity
    }

    const randomIndex = Math.floor(Math.random() * possibleCharges.length);
    return possibleCharges[randomIndex];
}

