/// Function to get selected items from the deepest (third) level
function getSelectedDeepestItems() {
  const categoryCheckboxes = document.querySelectorAll('.dropdown-checkbox.category');
  const typeCheckboxes = document.querySelectorAll('.dropdown-checkbox.type');
  const itemCheckboxes = document.querySelectorAll('.dropdown-checkbox.item');

  const checkedCategories = [...categoryCheckboxes].filter(cb => cb.checked).map(cb => cb.value);
  const checkedTypes = [...typeCheckboxes].filter(cb => cb.checked).map(cb => cb.value);
  const checkedItems = [...itemCheckboxes].filter(cb => cb.checked).map(cb => cb.value);

  const typeMap = new Map();
  typeCheckboxes.forEach(cb => typeMap.set(cb.value, cb.dataset.parent));

  const itemMap = new Map();
  itemCheckboxes.forEach(cb => itemMap.set(cb.value, cb.dataset.parent));

  // If nothing is checked, return all items
  if (checkedCategories.length === 0 && checkedTypes.length === 0 && checkedItems.length === 0) {
    return [...itemMap.keys()];
  }

  const selected = new Set();

  // 1. Always prioritize most specific: explicitly selected items
  checkedItems.forEach(item => selected.add(item));

  // 2. Add items whose data-parent is in checkedTypes, but only if no child item under that type is selected
  const typesWithChildSelected = new Set(checkedItems.map(item => itemMap.get(item)));
  checkedTypes.forEach(type => {
    if (!typesWithChildSelected.has(type)) {
      itemCheckboxes.forEach(cb => {
        if (cb.dataset.parent === type) {
          selected.add(cb.value);
        }
      });
    }
  });

  // 3. Add items under types whose data-parent is in checkedCategories, 
  // only if no type or item under that category is already selected
  const categoriesWithChildTypeSelected = new Set(checkedTypes.map(type => typeMap.get(type)));
  const categoriesWithGrandchildItemSelected = new Set(
    checkedItems.map(item => typeMap.get(itemMap.get(item)))
  );

  checkedCategories.forEach(category => {
    if (!categoriesWithChildTypeSelected.has(category) && !categoriesWithGrandchildItemSelected.has(category)) {
      itemCheckboxes.forEach(cb => {
        const itemType = cb.dataset.parent;
        const itemCategory = typeMap.get(itemType);
        if (itemCategory === category) {
          selected.add(cb.value);
        }
      });
    }
  });

  return [...selected];
}

