//testCategoryDistribution(1000);
function testCategoryDistribution(runs = 1000) {
  const categoryCount = {};

  for (let i = 0; i < runs; i++) {
    // Create weighted pool
    const weightedCategoryPool = [];
    for (const [category, weight] of Object.entries(categoryWeights)) {
      for (let j = 0; j < weight; j++) {
        weightedCategoryPool.push(category);
      }
    }

    // Randomly choose a category
    const chosenCategory = weightedCategoryPool[Math.floor(Math.random() * weightedCategoryPool.length)];

    // Count the result
    categoryCount[chosenCategory] = (categoryCount[chosenCategory] || 0) + 1;
  }

  console.log(`Results after ${runs} simulated rolls:`);
  console.table(categoryCount);
}

